import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Person } from '../login/login.page';
import { StorageMap } from '@ngx-pwa/local-storage';
import { HttpClient } from '@angular/common/http';
import { Plugin } from '@capacitor/core';
import {Capacitor}

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  data!: Person;
  firstName! : string
  lastName! : string

  firstVal = ""
  video1 = ""
  secondVal = ""
  secondContentVal = ""

  constructor(
    private route: ActivatedRoute, 
    private router: Router, 
    private storage : StorageMap,
    private http: HttpClient,
    ) {
    storage.get("Users").subscribe((users) => {
      console.log(users)
      let user = JSON.stringify(users)
      this.data = JSON.parse(user)
      this.firstName = this.data.firstName
      this.lastName = this.data.lastName
    })

    this.firstVal = "RSPRO"
    this.video1 = "Introducing: RepairSolutionsPRO"
    this.secondVal = "Innova SDS43"
    this.secondContentVal = "Innova SDS43 | SDS-Inspector"


    http.get("https://dev-api-pro.repairsolutions.com/app1.1/api/supports/tutorials").toPromise().then((data: any) => {
      let tutorials: Tutorial = data
      console.log(tutorials.message.code)
      console.log(tutorials.tutorialVideos.length)
    });

  }

  SignOut() {
    this.storage.delete('Users').subscribe(() => {});
    this.router.navigate(['/login']);
  }

  playVideo() {
    console.log("https://youtu.be/H8jl8LplZag")
  }

  public alertButtons = [
    {
      text: 'Cancel',
      role: 'cancel',
      handler: () => {
        console.log('Alert canceled');
      },
    },
    {
      text: 'OK',
      role: 'confirm',
      handler: () => {
        console.log('Alert confirmed');
        this.SignOut()
      },
    },
  ];

}


export interface Tutorial {
  tutorialVideos:   TutorialVideo[];
  tutorialArticles: TutorialArticle[];
  tutorialManuals:  TutorialArticle[];
  message:          Message;
}

export interface Message {
  code:        number;
  description: string;
}

export interface TutorialArticle {
  title: string;
  link:  string;
}

export interface TutorialVideo {
  name:   string;
  videos: TutorialArticle[];
}
